function bandicon($data,$img=false,$si=false){                                                                                                                                                                                                                                                                                                                               
if($data[1] &&  $data[3] !== off){                                                                                                                                                                                                                                                                                                                                           
$dl = explode("|",$data[3]);                                                                                                                                                                                                                                                                                                                                                 
$ahits=explode('()', $data[6]);                                                                                                                                                                                                                                                                                                                                              
$b_lmt = time()-$dl[1]*$dl[2];                                                                                                                                                                                                                                                                                                                                               
$hi=0;                                                                                                                                                                                                                                                                                                                                                                       
$within=0;                                                                                                                                                                                                                                                                                                                                                                   
while($hi++ < count($ahits) ){                                                                                                                                                                                                                                                                                                                                               
$usr = explode("|",$ahits[$hi]);                                                                                                                                                                                                                                                                                                                                             
if($usr[3]>$b_lmt)$within++;                                                                                                                                                                                                                                                                                                                                                 
if($within == $dl[0]){  $limit=true;  break;}                                                                                                                                                                                                                                                                                                                                
}// end loop                                                                                                                                                                                                                                                                                                                                                                 
$icon = '<img ';                                                                                                                                                                                                                                                                                                                                                             
if($limit){                                                                                                                                                                                                                                                                                                                                                                  
$icon .=  "src=\"downstat_art/bandactive.gif\" border=0 alt=\"Bandwidth is over the limit\" $img";                                                                                                                                                                                                                                                                           
}else{// if limit                                                                                                                                                                                                                                                                                                                                                            
if($dl[4])$icon .=  "src=\"downstat_art/bandhasactivated.gif\" border=0 alt=\"Bandwidth has been over the limit\" $img"; else $icon .= "src=\"downstat_art/band.gif\" border=0 alt=\"Bandwidth is set\" $img";                                                                                                                                                               
}// if !limit                                                                                                                                                                                                                                                                                                                                                                
$icon .= ' >';                                                                                                                                                                                                                                                                                                                                                               
}else{                                                                                                                                                                                                                                                                                                                                                                       
if($si)$icon .= '<img src="downstat_art/band.gif" '.$img.' border=0>';                                                                                                                                                                                                                                                                                                       
}// if data[3]                                                                                                                                                                                                                                                                                                                                                               
return $icon;                                                                                                                                                                                                                                                                                                                                                                
}//                                                                                                                                                                                                                                                                                                                                                                          
function mailicon($br,$extra,$alwayson){                                                                                                                                                                                                                                                                                                                                     
