if($br[4] == off){                                                                                                                                                                                                                                                                                                                                                           
if(!$alwayson){return "<img src=\"downstat_art/hit_email.gif\" border=0 $extra >"; }                                                                                                                                                                                                                                                                                         
}elseif($hits < $br[4]){                                                                                                                                                                                                                                                                                                                                                     
return "<img src=\"downstat_art/hit_email.gif\" border=0 $extra >";                                                                                                                                                                                                                                                                                                          
}elseif($hits > $br[4]){                                                                                                                                                                                                                                                                                                                                                     
return "<img src=\"downstat_art/emailover.gif\" border=0 $extra >";                                                                                                                                                                                                                                                                                                          
}else{                                                                                                                                                                                                                                                                                                                                                                       
if(!$alwayson){return "<img src=\"downstat_art/hit_email.gif\" border=0 $extra >"; }                                                                                                                                                                                                                                                                                         
}                                                                                                                                                                                                                                                                                                                                                                            
}// end of function mailicon                                                                                                                                                                                                                                                                                                                                                 
