function datezone($in=false,$sntx=false,$tog=false){                                                                                                                                                                                                                                                                                                                         
global $timeinput,$stroftime,$keytime,$fndtime,$sntxin;    
                                                                                                                                                                                                                                                                                                              
if($in == false)$in= time();                                                                                                                                                                                                                                                                                                                                                 
$stroftime = str_replace($fndtime,$keytime,$sntxin);                                                                                                                                                                                                                                                                                                                         
if($sntx == false)$sntx= $stroftime;                                                                                                                                                                                                                                                                                                                                         
if(!$tog ){                                                                                                                                                                                                                                                                                                                                                                  
$dte=explode(':',ereg_replace("(\-|\+)",'',$timeinput));                                                                                                                                                                                                                                                                                                                     
$sec= ($dte[0]*3600)+($dte[1]*60);                                                                                                                                                                                                                                                                                                                                           
}                                                                                                                                                                                                                                                                                                                                                                            
if(strstr($timeinput, '-')){                                                                                                                                                                                                                                                                                                                                                 
if($sntx == THROUGH) return $in-$sec;                                                                                                                                                                                                                                                                                                                                        
return date($sntx,$in-$sec);                                                                                                                                                                                                                                                                                                                                                 
}else{                                                                                                                                                                                                                                                                                                                                                                       
if($sntx == THROUGH) return $in+$sec;                                                                                                                                                                                                                                                                                                                                        
return date($sntx,$in+$sec);                                                                                                                                                                                                                                                                                                                                                 
}                                                                                                                                                                                                                                                                                                                                                                            
}// end of function datezone                                                                                                                                                                                                                                                                                                                                                 
function words($number, $word,$extra=false){                                                                                                                                                                                                                                                                                                                                 
if(!$number)$number=0;                                                                                                                                                                                                                                                                                                                                                       
if($extra){  $b1="<$extra>"; $b2="</$extra>"; }                                                                                                                                                                                                                                                                                                                              
if($number == 1){ return $b1.$number.$b2." $word"; }else{ return $b1.$number.$b2." $word"."s"; }                                                                                                                                                                                                                                                                             
}                                                                                                                                                                                                                                                                                                                                                                            
function showastime($inputseconds,$intime=false){                                                                                                                                                                                                                                                                                                                            
if(!$intime)$intime=datezone(false,"THROUGH",false);                                                                                                                                                                                                                                                                                                                         
if($intime==TIME)$intime=time();                                                                                                                                                                                                                                                                                                                                             
$howlong = round( ($intime - $inputseconds) );                                                                                                                                                                                                                                                                                                                               
if("$howlong" < "60"){ $secelap = $howlong; $trange = "Second";  }                                                                                                                                                                                                                                                                                                           
if("$howlong" > "60"){ $secelap = round($howlong / 60); $trange = "Minute"; }                                                                                                                                                                                                                                                                                                
if("$howlong" > "3600"){ $secelap = round($howlong / 3600); $trange = "Hour"; }                                                                                                                                                                                                                                                                                              
if("$howlong" > "86400"){ $secelap = round($howlong / 86400); $trange = "Day"; }                                                                                                                                                                                                                                                                                             
if("$howlong" > "604800"){ $secelap = round($howlong / 604800); $trange = "Week"; }                                                                                                                                                                                                                                                                                          
return " ".words($secelap ,$trange)." Ago";                                                                                                                                                                                                                                                                                                                                  
}                                                                                                                                                                                                                                                                                                                                                                            
function str_short($in,$length=50,$mm=false){                                                                                                                                                                                                                                                                                                                                
if(strlen($in) >$length):                                                                                                                                                                                                                                                                                                                                                    
if($mm){                                                                                                                                                                                                                                                                                                                                                                     
$in= substr("$in", 0, ($length-3))."...";                                                                                                                                                                                                                                                                                                                                    
}else{                                                                                                                                                                                                                                                                                                                                                                       
$in= "...".substr("$in", $length-50, ($length-3));                                                                                                                                                                                                                                                                                                                           
}                                                                                                                                                                                                                                                                                                                                                                            
endif;                                                                                                                                                                                                                                                                                                                                                                       
return $in;                                                                                                                                                                                                                                                                                                                                                                  
}                                                                                                                                                                                                                                                                                                                                                                            
